import Modal from 'bootstrap/js/dist/modal';

export default {

  watch: {
    product() {
      this.tempProduct = this.product;
    },
  },
  methods: {
    showModal() {
      this.modal.show();
    },
    hideModal() {
      this.modal.hide();
    },
  },
  mounted() {
    console.log('delmodalt')
    this.modal = new Modal(this.$refs.modal);
  },
};
